select a.order_id as dmdId,a.create_time as createTime,a.deadline as deadLine,a.place_id as placeId,
a.type_id as typeId,a.title as title,a.content as content,a.price as price,a.dmder_id as dmderId,b.user_name as dmderName,
b.credit as dmderStar,b.user_img as dmderImg
from orders a left join user b on a.dmder_id=b.user_id where a.status=1 and a.university_id=#{universityId}

  and a.place_id in

    #{item}



  and a.type_id in

    #{item}



  and a.deadline>=#{lowDeadline}


  and a.deadline<=#{highDeadline}


  and  a.price>=#{lowPrice}


  and a.price<=#{highPrice}




limit #{pageNow},10

