select a.order_id as serviceId,a.create_time as lowDeadline,a.deadline as highDeadline,a.place_id as placeId,
a.type_id as typeId,a.title as title,a.content as content,a.price as price,a.servant_id as servantId,b.user_name as servantName,
b.credit as servantStar,b.user_img as servantImg
from orders a left join user b on a.servant_id=b.user_id where a.status=0 and a.university_id=#{universityId}

  and a.place_id in

    #{item}



  and a.type_id in

    #{item}



  and a.deadline >= #{lowDeadline}


  and a.deadline <= #{highDeadline}


  and  a.price >= #{lowPrice}


  and a.price <= #{highPrice}




limit #{pageNow},10

